import React, { useState, useRef, useEffect }from "react";
import "./coffeeslider.css";
import Modal from "./modal.js";


const CoffeeSlider = (props) => {

  const {
        step,
        min,
        max,
        defaultValue,
        value,
        onChangeValue,
        openModal,
    } = props;

    const rangeRef = useRef(onChangeValue);
    const [range, setRange] = useState(defaultValue);
    const handleChange = max => e =>{
        onChangeValue(e)
        setRange(e.target.value);
    }
    const [rangeValue, setRangeValue] = useState(10);
    const [show, setShow, isOpen] = useState(false);

    const onChangeSlider = (e) => {
      console.log(rangeValue);
      setRangeValue(parseInt(e.target.value, 10));
     /*  if (rangeValue > 18) {
        let openModal = setShow(true);
      } */
    };

       /* when slider value > 20 when stops - talk to DOM - open modal */

    return (
      <>
        <div className="coffecup">
          Cups of Joe
          <input
            ref={rangeRef}
            className="cupsofjoe"
            type="range"
            step={step}
            min={min}
            max={max}
            /* defaultValue = '1' */
            value={value}
            onChange={handleChange(max)}
          />
          <span className="range-slider-value">{range}</span>
        </div>
      </>
    );
}
export default CoffeeSlider;
