import React, { useState, useRef, useEffect }from "react";
import "./coffeeslider.css";
import Modal from "./modal.js";

const CoffeeSlider = (props) => {
    const {
        step,
        min,
        max,
        defaultValue,
        value,
        onChangeValue,
    } = props;

    const rangeRef = useRef();
    const [range, setRange] = useState(defaultValue);
    const handleChange = max => e =>{
        onChangeValue(e)
        setRange(e.target.value);
    }

    const modalPopUp = value => e => {
        onChangeValue (e.target.value > 20) ; setShow(true)
      }
       /* when slider value > 20 when stops - talk to DOM - open modal */

    return (
      <>
        <div className="coffecup">
          Cups of Joe
          <input
            ref={rangeRef}
            className="cupsofjoe"
            type="range"
            step={step}
            min={min}
            max={max}
            defaultValue = '1'
            value={value}
            onChange={handleChange(max)}
          />
          <span className="range-slider-value">{range}</span>
        </div>
      </>
    );
}
export default CoffeeSlider;
