 /* async function coffeeStops({rangeValue}) { */
           const coffeeDistance = intCoffee /(rangeValue); 

        /*   await calculatePath;
          if (distance > 0) {
            console.log(coffeeDistance)
            return coffeeDistance;
            console.log({distance}); */


/* Micah's Code */
      const directions = {};     
      const stops = 2;
      const route = setDirectionsResponse(results)  

      route.rendering.routeId = `thisRoute`;
      // Set the stops for the route to the first value (default)
      route.rendering.stops = stops;
      // When this select element is changed, update the route renderer.stops to the new value

        // update the route renderer.stops to the new value
        directions[this.name].renderer.stops = this.value;
        // Draw the new markers for the route that just had it's stops changed
        setMarkers(this.name)
      };         
       requestRoute(route,map);



  // Set markers for the route with ID
  const setMarkers = (ID) => {
    // Get info about the route
    const direction = directions[ID],
        renderer = direction.renderer,
        stop = renderer.stops,
        marker = renderer.marker,
        map = renderer.getMap(),
        dirs = direction.renderer.getDirections();
        marker.map = map;

    // For each set, which is an object where key: number of stops and value: list of markers
    for (const k in direction.sets) {
      const set = directions[ID].sets[k];
      // Only show the set of markers that match the currently selected number of stops
      set.visible = !!(k === stop);

      // For each marker in the set
      set.forEach(marker => {
        // If this is the visible set, set the marker map to the map otherwise null
        marker.setMap(set.visible ? map : null);
      });
    };

    // If a set of markers has not been calculated for this number of stops yet
    if(!direction.sets[stop]) {
      if(dirs.routes.length) {
        const route = dirs.routes[0];
        let distanceBetweenStops = 0;

        // For each leg in the route
        route.legs.forEach(leg => {
          if(leg.distance) {
            // Set the distance between stops to (total leg distance) / (number of stops + 1)
            distanceBetweenStops = leg.distance.value / (parseInt(stop) + 1);
          }

        });

        // Get the list of markers for this milestone
        direction.sets[stop]= getMarkers(route, distanceBetweenStops, marker);
      }
    }
  }

  // Remove old markers and render updated map with markers
   const requestRoute = (route,map) => {
    if(!window.gDirSVC) window.gDirSVC = new google.maps.DirectionsService();

    var renderer=new google.maps.DirectionsRenderer(route.rendering);
     var renderer=new google.maps.DirectionsRenderer(route.rendering);
        renderer.setMap(map);
        renderer.setOptions({preserveViewport:true})


    google.maps.event.addListener(renderer, 'directions_changed', function() {

          if(directions[this.routeId])
          {
           //remove markers
            for(var k in directions[this.routeId].sets)
            {              
                for(var m=0;m<directions[this.routeId].sets[k].length;++m)
                {
                  directions[this.routeId].sets[k][m].setMap(null);
                }
            }
          }

          directions[this.routeId]={renderer:this,sets:{}};
          setMarkers(this.routeId);

    });

     window.gDirSVC.route(route.request, function(response, status) {
      if (status == google.maps.DirectionsStatus.OK) {
        renderer.setDirections(response);
    }
    });



